var x,y, rotation;
function addAGV(x, y, rotation, index){
    msg.payload = json.AGV;
    msg.payload[0].elementId = "ID_AGV_" + String(index);
    msg.payload[1].elementId = "ID_AGV_" + String(index);
    msg.payload[2].elementId = "ID_AGV_" + String(index);

    msg.payload[0].elementAttributes.x = ORIGIN_X + x * grid;
    msg.payload[0].elementAttributes.y = ORIGIN_Y + y * grid;
    msg.payload[0].elementAttributes.width = grid;
    msg.payload[0].elementAttributes.height = grid;

    msg.payload[1].elementAttributes.cx = ORIGIN_X + x * grid + Math.floor(grid / 2);
    msg.payload[1].elementAttributes.cy = ORIGIN_Y + y * grid + Math.floor(grid / 2);
    msg.payload[1].elementAttributes.r = Math.floor(grid / 2);

    msg.payload[2].elementAttributes.x1 = ORIGIN_X + x * grid + Math.floor(grid / 2);
    msg.payload[2].elementAttributes.y1 = ORIGIN_Y + y * grid + Math.floor(grid / 2);

    if( rotation === 0){
        msg.payload[2].elementAttributes.x2 = ORIGIN_X + x * grid + Math.floor(grid / 2) ;
        msg.payload[2].elementAttributes.y2 = ORIGIN_Y + y * grid ;
    }
    else if( rotation === 90){
        msg.payload[2].elementAttributes.x2 = ORIGIN_X + x * grid + Math.floor(grid / 2) + Math.floor(grid / 2);
        msg.payload[2].elementAttributes.y2 = ORIGIN_Y + y * grid + Math.floor(grid / 2);
    }
    else if( rotation === 180){
        msg.payload[2].elementAttributes.x2 = ORIGIN_X + x * grid + Math.floor(grid / 2);
        msg.payload[2].elementAttributes.y2 = ORIGIN_Y + y * grid + Math.floor(grid / 2) + Math.floor(grid / 2);
    }
    else if( rotation === 270){
        msg.payload[2].elementAttributes.x2 = ORIGIN_X + x * grid + Math.floor(grid / 2) - Math.floor(grid / 2);
        msg.payload[2].elementAttributes.y2 = ORIGIN_Y + y * grid + Math.floor(grid / 2);
    }
    node.send(msg);
}

function moveAGV(x, y, rotation, index){
    msg.payload = json.Remove;
    msg.payload.elementId = "AGV_" + String(index);
    node.send(msg);
    addAGV(x, y, rotation, index);
}
